function newItem() {
  let t = $("<li></li>"),
    e = $("#input").val();
  t.append(e),
    "" === e ? alert("You must write something!") : $("#list").append(t),
    t.on("dblclick", function () {
      t.addClass("strike");
    });
  let n = $("<crossOutButton></crossOutButton>");
  function l() {
    t.addClass("delete");
  }
  n.append(document.createTextNode("X")),
    t.append(n),
    n.on("click", function () {
      t.addClass("delete");
    }),
    $("#list").sortable();
}
